(function ($) {
    $(document).ready(function(){

        var leftZipCodeCheckForm = new VarienForm('form-zipCodeCheck');
        var formLeftCheckZipCodeExists = new VarienForm('form-left-check-zipcode-exists');
        var formLeftCheckZipCodeNotExists = new VarienForm('form-left-check-zipcode-not-exists');

        var onZipCodeExists = function (zipCode, days) {
            $.fancybox.close();
            var el = $('#left-anchor-check-zipcode-exists').get(0);

            $('#left-check-zipcode-exists .currentZipCode').html(zipCode);
            $('#leftCurrentDelivery').html(days2html(days));

            if (el)
                $(el).trigger('click');
        };

        var onZipCodeNotExists = function (zipCode, days) {
            $.fancybox.close();
            var el = $('#left-anchor-check-zipcode-not-exists').get(0);

            $('#left-check-zipcode-not-exists .currentZipCode').html(zipCode);

            if (el)
                $(el).trigger('click');
        };

        var isSending = false;

        var days2html = function (days) {
            var i = 0, len = days.length, html = '';

            for (; i < len; ++i) {
                html += '<span>' + days[i].slice(0, 3).toUpperCase() + '</span>';
            }

            return html;
        };

        var checkZipCode = function (url, zipCode, onZipCodeExists, onZipCodeNotExists, beforeSend, complete) {

            $.ajax({
                url: url,
                type:'POST',
                data:{'zipCode':zipCode},
                dataType:'json',
                error:function (a,b,c,d) {
                    alert('Something went wrong! Please refresh your browser and try again.');
                },
                success:function (data) {

                    if (data.length) {
                        typeof onZipCodeExists === 'function' && onZipCodeExists.apply(null, [zipCode, data]);
                    } else {
                        typeof onZipCodeNotExists === 'function' && onZipCodeNotExists.apply(null, [zipCode, data]);
                    }
                },
                beforeSend:function () {
                    typeof beforeSend === 'function' && beforeSend();
                },
                complete:function () {
                    typeof complete === 'function' && complete();
                }
            });
        };

        var onBtnLeftCheckZipCodeClick = function (e) {
            var $overlay = null;
            if (leftZipCodeCheckForm.validator.validate() && !isSending) {
                var $form = $(leftZipCodeCheckForm.form);
                var zipCode = $('#iptLeftZipCode', $form).val();
                var url = $(e.currentTarget).data('url');
                checkZipCode(url, zipCode, onZipCodeExists, onZipCodeNotExists,
                    function () {
                        $overlay = $('<div class="fancybox-overlay fancybox-overlay-fixed"><div class="loading">&nbsp;</div></div>').appendTo('body').show();
                        isSending = true;
                    }, function () {
                        $overlay.remove();
                        isSending = false;
                    });

            }
            e.stopPropagation();
            e.preventDefault();
            return false;
        };

        $('.btnLeftCheckZipCode').bind('click', onBtnLeftCheckZipCodeClick);

        var addZipCodeSubscribe = function (url, params, beforeSend, complete) {

            $.ajax({
                url: url,
                type:'POST',
                data:params,
                dataType:'json',
                error:function () {
                    alert('Network error!');
                },
                success:function (data) {
                    if (!data.status) {
                        alert(data.msg);
                    }else{
                        $(".zipcodenotexists").html("<ul class='messages'><li class='success-msg'><ul><li><span>Thank you for your subscription.</span></li></ul></li></ul>");
                    }
                },
                beforeSend:function () {
                    typeof beforeSend === 'function' && beforeSend();
                },
                complete:function () {
                    typeof complete === 'function' && complete();
                }
            });
        };

        $('#btnLeftAddZipCodeSubscribe').click(function () {
            if (formLeftCheckZipCodeNotExists.validator.validate()) {
                var $form = $(leftZipCodeCheckForm.form);
                var zipCode = $('#iptLeftZipCode', $form).val();
                var email = $('#iptLeftAddZipCodeSubscribeEmail').val();
                var url = $(this).data('url');

                addZipCodeSubscribe(url, {
                    'zipCode':zipCode,
                    'email':email
                });
            }

            return false;
        });

        $('#iptLeftZipCode')
            .mouseenter(function(){
                $(this).css('borderColor','#FF6600');
            })
            .mouseleave(function(){
                $(this).css('borderColor', '');
            })
        ;

        $('.fancybox').fancybox();
    });
})(jQuery);

