(function($) { 
	$(function() {

        var $slider = $('#focus');

        if($slider.length < 1) return;

        var $slider_list = $('ul', $slider);

        var slider_width = $slider.outerWidth(true);
		var slide_count = $('li', $slider).length;
		var index = 0;
		var picTimer;

        //generate controls
        var $btn_container = $('<div>').addClass('btn')
        for(var i=0; i < slide_count; i++) {
		    $btn_container.append($('<span>'));
        }
		$slider.append($btn_container);
		//

        var $btns = $(".btn span", $slider);
        var $slide_containers = $('li div', $slider_list);

		$btns.mouseenter(function() {
			index = $btns.index(this);
			showSlide(index);
		}).eq(0).trigger("mouseenter");

		$slider_list.css("width", slider_width * (slide_count + 1));
        $slide_containers.hover(function() {
			$(this).siblings().css("opacity",1);
		},function() {
            $slide_containers.css("opacity",1);
		});

		$slider.hover(function() {
			clearInterval(picTimer);
		},function() {
			picTimer = setInterval(function() {
				if(index == slide_count) {
					showFirstSlide();
					index = 0;
				} else { 
					showSlide(index);
				}
				index++;
			},7000); 
		}).trigger("mouseleave");

		function showSlide(index) {
			var nowLeft = -index * slider_width;
			$slider_list.stop(true,false).animate({"left":nowLeft},505); 
			$btns.removeClass("on").eq(index).addClass("on"); 
		}

		function showFirstSlide() {
			$slider_list.append($("li:first", $slider_list).clone());
			var nowLeft = -slide_count * slider_width;
            $slider_list.stop(true,false).animate({"left":nowLeft},505,function() {

                $slider_list.css("left","0");
				$("li:last", $slider_list).remove();
			}); 
			$btns.removeClass("on").eq(0).addClass("on");
		}
	});
})(jQuery);
