(function (win) {
    var UrlManager = function (url) {
        var _url = url || win.location.href;
        var _reg = /^(ftp|http|https):\/\/([a-zA-Z0-9.-]+)(:(\d+))?\/([^?#]+)?(\?([^#]*))?(#(.*))?$/;
        if (_reg.test(_url)) {
            var _that = this;
            _url.replace(_reg, function (match, p1, p2, p3, p4, p5, p6, p7, p8, p9) {
                _that.protocol = p1;
                _that.host = p2;
                _that.port = p4 || '';
                _that.path = p5 || '';
                _that.params = p7 || {};
                _that.anchorParams = p9 || {};
            });

            if (typeof this.params === 'string') {
                var _keyValCollection = this.params.split('&');
                this.params = {};

                for (var i = 0, len = _keyValCollection.length; i < len; ++i) {
                    var _keyVal = _keyValCollection[i].split('=');
                    this.params[_keyVal[0]] = _keyVal[1];
                }
            }

            if (typeof this.anchorParams === 'string') {
                _keyValCollection = this.anchorParams.split('&');
                this.anchorParams = {};

                for (i = 0, len = _keyValCollection.length; i < len; ++i) {
                    _keyVal = _keyValCollection[i].split('=');
                    this.anchorParams[_keyVal[0]] = _keyVal[1];
                }
            }

        } else {
            throw new Error('provide a error url.');
        }
    };

    UrlManager.prototype.get = function (property) {
        if (this.hasOwnProperty(property))
            return this[property];
        return null;
    };

    UrlManager.prototype.set = function (property, val) {
        this[property] = val;
        return this;
    };

    UrlManager.prototype.getParam = function (param) {
        var _params = this.params;
        if (_params.hasOwnProperty(param))
            return _params[param];
        return null;
    };

    UrlManager.prototype.setParam = function (param, val) {
        this.params[param] = val;
        return this;
    };

    UrlManager.prototype.getAnchorParam = function (param) {
        var _params = this.anchorParams;
        if (_params.hasOwnProperty(param))
            return _params[param];
        return null;
    };

    UrlManager.prototype.setAnchorParam = function (param, val) {
        this.anchorParams[param] = val;
        return this;
    };

    UrlManager.prototype._params2string = function (params) {
        var _params = '';

        for (var p in params) {
            if (params.hasOwnProperty(p) && typeof params[p] !== 'function' && params[p] !== null) {
                _params += p + '=' + params[p] + '&';
            }
        }

        if (_params)
            _params = _params.replace(/&$/, '');

        return _params;
    };

    UrlManager.prototype.toString = function () {
        var _url = this.protocol + '://' + this.host;
        if (this.port)
            _url += ':' + this.port;
        _url += '/' + this.path;

        var _params = this._params2string(this.params);
        if (_params)
            _url += '?' + _params;

        _params = this._params2string(this.anchorParams);
        if (_params)
            _url += '#' + _params;

        return _url;
    };

    win.UrlManager = UrlManager;
})(window);
(function () {
    var tinyCookie = {};

    tinyCookie.set = function (sName, sValue, expires) {
        document.cookie = sName + "=" + encodeURIComponent(sValue);
        expires = new Date((new Date()).getTime() + expires);
        document.cookie += ("; expires=" + expires.toGMTString());
    };

    tinyCookie.get = function (sName) {
        var aCookie = document.cookie.split("; ");
        for (var i = 0; i < aCookie.length; i++) {
            var aCrumb = aCookie[i].split("=");
            if (sName == aCrumb[0])
                return decodeURIComponent(aCrumb[1]);
        }
        return null;
    };

    tinyCookie.del = function (sName) {
        var expires = new Date((new Date()).getTime() - 1);
        document.cookie = sName + "=; expires=" + expires.toGMTString();
    };

    window.tinyCookie = tinyCookie;
})();
